import React, { useState, useEffect } from "react";
import config from "../config";
import Cookie from "js-cookie";
import { FormGroup, FormControl } from "react-bootstrap";
import LoaderButton from "../components/LoaderButton";
import "./Archive.css";

export default function Archives(props) {
  const [content, setContent] = useState("");
  const [staple, setStaple] = useState({});
  const [isDeleting, setIsDeleting] = useState(false);

  useEffect(() => {
    async function onLoad() {
      try {
        fetch(config.HOST+`/rest/api/1/staples/archive`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer ' + Cookie.get('token'),
          },
        }).then(response => response.json())
          .then(data => {
            if (data.staple) {
              setContent(data.staple.content);
              setStaple(data.staple);
            } else {
              alert(`Staple with id ${props.match.params.id} not found!`);
            }
          })
          .catch(e => alert(e.message));
      } catch (e) {
        alert(e);
      }
    }
    onLoad();
  }, [props.match.params.id]);

  async function handleDelete(event) {
    setIsDeleting(true);
    event.preventDefault();
    try {
      fetch(config.HOST+`/rest/api/1/staple/${props.match.params.id}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ' + Cookie.get('token'),
        },
      }).then(response => {
        if (response.ok) {
          setIsDeleting(false);
          props.history.push('/')
        }
      }).catch(e => alert(e.message));
    } catch (e) {
      alert(e);
    }
  }

  return (
    <div className="Staples">
      {staple && (
        <form>
          <FormGroup controlId="content">
            <FormControl
              // plaintext={content}
              componentClass="textarea"
              plaintext={content}
              defaultValue={content}
            />
          </FormGroup>
          <LoaderButton
            block
            bsSize="large"
            bsStyle="danger"
            onClick={handleDelete}
            isLoading={isDeleting}
          >
            Delete
          </LoaderButton>
        </form>
      )}
    </div>
  );
}
