import React, { useState } from "react";
import { FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import LoaderButton from "../components/LoaderButton";
import { useFormFields } from "../libs/hooksLib";
import "./Login.css";
import Cookie from "js-cookie";
import config from "../config";

export default function Login(props) {
  const [isLoading, setIsLoading] = useState(false);
  const [isResetting, setIsResetting] = useState(false);
  const [fields, handleFieldChange] = useFormFields({
    email: "",
    password: ""
  });

  function validateForm() {
    return fields.email.length > 0 && fields.password.length > 0;
  }

  async function handleSubmit(event) {
    event.preventDefault();
    setIsLoading(true);

    try {
      // await Auth.signIn(email, password);
      // fields.email; fields.password --> This is what I will need to pass down the chain.
      fetch(config.HOST+'/get-token', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: fields.email,
          password: fields.password,
        }),
      }).then((response) => {
        if (response.ok) {
          response.json().then(responseJson => {
            // store the token in a cookie.
            Cookie.set("token", responseJson.token);
            props.userHasAuthenticated(true);
          })
        } else {
          alert("Login failed.")
        }
      })
      .catch((error) => {
        alert(error.message);
      });
      // probably will create a state and store it in state/
    } catch (e) {
      alert(e.message);
    } finally {
      setIsLoading(false);
    }
    // If everything worked out fine, we should get back the JWT token and store it for further api calls.
  }


  function resetPassword() {
    setIsResetting(true);
    try {
      fetch(config.HOST+"/reset", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: fields.email,
        }),
      }).then(response => {
        if (response.ok) {
          setIsResetting(false);
          props.history.push("/login");
          window.location.reload();
        } else {
          setIsResetting(false);
          alert("Failed to reset password.");
        }
      }).catch(e => {
        alert(e.message);
        setIsResetting(false)
      })
    } catch (e) {
      alert(e);
      setIsResetting(false);
    }
  }

  return (
    <div className="Login">
      <form>
        <FormGroup controlId="email" bsSize="large">
          <ControlLabel>Email</ControlLabel>
          <FormControl
            autoFocus
            type="email"
            value={fields.email}
            onChange={handleFieldChange}
          />
        </FormGroup>
        <FormGroup controlId="password" bsSize="large">
          <ControlLabel>Password</ControlLabel>
          <FormControl
            value={fields.password}
            onChange={handleFieldChange}
            type="password"
          />
        </FormGroup>
        <LoaderButton
          block
          type="info"
          bsSize="large"
          isLoading={isResetting}
          onClick={resetPassword}
        >
          Reset Password
        </LoaderButton>
        <LoaderButton
          block
          type="submit"
          bsSize="large"
          isLoading={isLoading}
          disabled={!validateForm()}
          onClick={handleSubmit}
        >
          Login
        </LoaderButton>
      </form>
    </div>
  );
}
