import React, {useEffect, useState} from "react";
import {FormControl, FormGroup, Grid, ListGroup, ListGroupItem, PageHeader} from "react-bootstrap";
import {LinkContainer} from "react-router-bootstrap";
import "./Home.css";
import config from "../config";
import Cookie from "js-cookie";
import LoaderButton from "../components/LoaderButton";

export default function Home(props) {
  const [staples, setStaples] = useState([]);
  const [nextStaple, setNextStaple] = useState({});
  const [isLoading, setIsLoading] = useState(true);

  function renderStapleView(astaples) {
    let list = undefined;
    if (astaples.staples) {
      list = astaples.staples.map(staple => {
        return (<LinkContainer key={staple.id} to={`/staples/${staple.id}`}>
          <ListGroupItem header={staple.name} key={staple.id}>
            {"Created: " + new Date(staple.created_at).toLocaleString()}
          </ListGroupItem>
        </LinkContainer>)
      })
    }
    let s = undefined;
    if (nextStaple.staple) {
      s = (
        <div className="Staples">
          <form>
            <FormGroup controlId="content">
              <FormControl
                plaintext={nextStaple.staple.content}
                readOnly
                defaultValue={nextStaple.staple.content}
              />
            </FormGroup>
            <LoaderButton
              block
              bsSize="large"
              bsStyle="warning"
              // onClick={handleArchive}
              // isLoading={isLoading}
            >
              Archive
            </LoaderButton>
            <LoaderButton
              block
              bsSize="large"
              bsStyle="danger"
              // onClick={handleDelete}
              // isLoading={isDeleting}
            >
              Delete
            </LoaderButton>
          </form>
        </div>
      )
    }
    return (
      <div>
        <Grid>
          <Row className="show-grid">
            <Col xs={12} md={8}>
              {s}
            </Col>
            <Col xs={6} md={4}>
              {list}
            </Col>
          </Row>
        </Grid>
        <LinkContainer key="new" to="/staples/new">
          <ListGroupItem>
            <h4>
              <b>{"\uFF0B"}</b> Create a new staple
            </h4>
          </ListGroupItem>
        </LinkContainer>
      </div>
    )
  }

  function renderLander() {
    return (
      <div className="lander">
        <h1>Staple</h1>
        <p>A stack based bookmark.</p>
      </div>
    );
  }

  function renderStaples() {
    return (
      <div className="staples">
        <PageHeader>Your Staples</PageHeader>
        <ListGroup>
          {!isLoading && renderStapleView(staples)}
        </ListGroup>
      </div>
    );
  }

  useEffect(() => {
    async function onLoad() {
      if (!props.isAuthenticated) {
        return;
      }

      try {
        fetch(config.HOST+"/rest/api/1/staple", {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer ' + Cookie.get('token'),
          },
        }).then(response => response.json())
          .then(data => setStaples(data))
          .catch(e => alert(e.message));
      } catch (e) {
        alert(e);
      }

      try {
        fetch(config.HOST+"/rest/api/1/staple/next", {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer ' + Cookie.get('token'),
          },
        }).then(response => response.json())
          .then(data => setNextStaple(data))
          .catch(e => alert(e.message));
      } catch (e) {
        alert(e);
      }

      setIsLoading(false);
    }

    onLoad();
  }, [props.isAuthenticated]);

  return (
    <div className="Home">
      {props.isAuthenticated ? renderStaples() : renderLander()}
    </div>
  );
}
